function selectRandomText(textArray) {
    return textArray[Math.floor(Math.random() * textArray.length)];
}

function loopingTypeEffect(elementId, textArray, typingSpeed, pauseDuration) {
    const elem = document.getElementById(elementId);
    let text = selectRandomText(textArray); // Select a random text initially
    let i = 0; // Initialize index for typing

    function type() {
        if (i < text.length) {
            elem.innerHTML += text.charAt(i);
            i++;
            setTimeout(type, typingSpeed); // Continue typing
        } else {
            setTimeout(() => {
                elem.innerHTML = ''; // Clear the text
                i = 0; // Reset index to start typing again
                text = selectRandomText(textArray); // Select a new random text
                setTimeout(type, pauseDuration); // Restart typing after the pause
            }, pauseDuration);
        }
    }
    type(); // Start typing effect
}

